mapper.select:
select rnum, ccr.* FROM (select rownum as rnum, cr.* FROM (SELECT r.RR_NO AS rrno, c.COS_TELNO, c.COS_L_NAME, c.COS_F_NAME, c.COS_EMAIL, c.COS_ALLERGY, r.RES_CHAIR, r.RR_CHILD, r.RR_ADULT, r.RR_DATE, r.RES_NAME, o.ORD_TABLE_NUM FROM RESTAURANT_RESERVATION r LEFT OUTER JOIN CUSTOMER c on(c.COS_NO = r.COS_NO) LEFT OUTER JOIN orders o on(o.RR_NO = r.RR_NO AND o.COS_NO = r.COS_NO)
to_char(RR_DATE,'YYYY-MM-dd') = #{query}
r.RR_DATE = to_date(#{query},'YYYY-MM-DD hh24:mi')
ORDER BY RRNO asc) cr WHERE rownum < = #{maxNum}) ccr WHERE rnum > = #{minNum}
